var loraId = global.get("loraId") //LoRa 선택 시 ID
var loraRelayNo = global.get("loraRelayNo") //LoRa Relay 번호
var deviceTag = global.get("deviceTag") // 사용할 기기 이름
var objectData = null
var twoDot = ':'
var minus = '-'

if ( deviceList == 'F:' && fanType == '') { //기기종류가 Fan인데 팬종류 입력값이 없을 경우
    global.set("errorCase", 'fanTypeError'); //에러발생 경고창 뜨도록
    objectData = 'Fan type unselected'; //"팬종류가 선택되어지지 않았습니다." 문장 생성 

} else if (deviceList == 'M:' && driveTime == ''){ //기기종류가 Motor인데 구동시간 입력값이 없을 경우
    global.set("errorCase", 'driveTimeError'); //에러발생 경고창 뜨도록
    objectData = 'Motor drive time undefined'; //"모터구동시간이 입력되지 않았습니다." 문장 생성

} else if (connectionType == 'G:' && gpioPin == ''){ //연결유형이 GPIO인데 GPIO핀 입력값이 없을 경우
    global.set("errorCase", 'gpioSelectError'); //에러발생 경고창 뜨도록
    objectData = 'GPIO Pin unselected'; //"GPIO 핀번호가 선택되지 않았습니다." 문장 생성

} else if (deviceList == 'M:' && gpioSubPin == ''){ //기기종류가 Motor인데 GPIO서브핀 입력값이 없을 경우
