    objectData = 'GPIO Second Pin unselected'; //"GPIO 서브핀번호가 선택되지 않았습니다." 문장 생성

} else if (connectionType == 'L:' && loraId == ''){ //연결유형이 LoRa인데 LoRa ID 입력값이 없을 경우
    global.set("errorCase", 'loraIdMissingError'); //에러발생 경고창 뜨도록
    objectData = 'LoRa id is undefined'; //"GPIO 서브핀번호가 선택되지 않았습니다." 문장 생성

} else if (connectionType == 'L:' && loraRelayNo == ''){ //기기종류가 Fan인데 팬종류 입력값이 없을 경우
